package ui.components

import javax.swing.ImageIcon
import javax.swing.Icon
import java.util.Base64
import java.awt.Image
import java.awt.image.BufferedImage
import java.awt.Color
import javax.imageio.ImageIO
import java.io.ByteArrayInputStream

object AppIcons {
    private const val MOUSE_POINTER_BASE64 = ""
    private const val HAND_BASE64 = ""
    private const val RULER_BASE64 = ""

    val MOUSE_POINTER: Icon = loadIcon(MOUSE_POINTER_BASE64)
    val HAND: Icon = loadIcon(HAND_BASE64)
    val RULER: Icon = loadIcon(RULER_BASE64)

    private fun loadIcon(base64Data: String): Icon {
        return try {
            val imageBytes = Base64.getDecoder().decode(base64Data)
            val inputStream = ByteArrayInputStream(imageBytes)
            var image = ImageIO.read(inputStream) ?: return ImageIcon()

            // Remove background (assume white or near-white background if not transparent)
            image = makeTransparent(image)

            // Resize to 24x24 for standard buttons (previously was 30x30, but 24x24 is more standard for 40x40 buttons)
            val scaledImage = image.getScaledInstance(24, 24, Image.SCALE_SMOOTH)
            ImageIcon(scaledImage)
        } catch (e: Exception) {
            ImageIcon()
        }
    }

    private fun makeTransparent(image: BufferedImage): BufferedImage {
        val width = image.width
        val height = image.height
        val transparentImage = BufferedImage(width, height, BufferedImage.TYPE_INT_ARGB)
        val g2d = transparentImage.createGraphics()
        g2d.drawImage(image, 0, 0, null)
        g2d.dispose()

        // If the image already has transparency (check a corner), we might not need to do anything
        // But the requirement says "ensure its background color is removed"
        // Let's assume the background is the color of the top-left pixel if it's not already transparent
        val backgroundRGB = transparentImage.getRGB(0, 0)
        
        // Only attempt to remove background if it looks like a solid background (not transparent)
        if ((backgroundRGB shr 24) and 0xFF > 250) { 
            for (y in 0 until height) {
                for (x in 0 until width) {
                    val rgb = transparentImage.getRGB(x, y)
                    if (isSimilar(rgb, backgroundRGB)) {
                        transparentImage.setRGB(x, y, 0x00FFFFFF and rgb)
                    }
                }
            }
        }
        return transparentImage
    }

    private fun isSimilar(rgb1: Int, rgb2: Int): Boolean {
        val r1 = (rgb1 shr 16) and 0xFF
        val g1 = (rgb1 shr 8) and 0xFF
        val b1 = rgb1 and 0xFF
        val r2 = (rgb2 shr 16) and 0xFF
        val g2 = (rgb2 shr 8) and 0xFF
        val b2 = rgb2 and 0xFF
        val threshold = 20
        return Math.abs(r1 - r2) < threshold && Math.abs(g1 - g2) < threshold && Math.abs(b1 - b2) < threshold
    }
}
